import { Container, Box, Typography, TextField, Button } from "@mui/material"
import casa from '../../midia/casa-de-saberes.jpg'
import volta from "../../midia/volta.png"
import { Link } from "react-router-dom"

import { Administradores } from "./adm"
import { useContext } from "react"
import Administrador from "../Login_Contexto/ContextoLogin";
import { useState } from "react";

import axios from "axios"


const Signin = () => {
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    const[usuarios,SetUsuarios] = useState ([])
    
    useEffect(
        () => {
            axios.get("http://localhost:3001/login/listar")
                .then(
                    (response) => {
                        SetUsuarios(response.data)
                        if(response.find(adm => adm.email == email && adm.tipo =='adm')){
                            console.log('achei um adm')

                        }
                    }
                )
                .catch(error => console.log(error))
        }
        ,
        []
    )

    const {mudarConta } = useContext(Administrador);

    const pegarEmail = (event) => {
        setEmail(event.target.value);
    };

    const pegarSenha = (event) => {
        setSenha(event.target.value);
    };

    const verificacao = () => {

        const admin = Administradores.find((admin) => admin.email === email && admin.senha === senha);
        if (admin) {
           mudarConta();
        } else {
            // Login falhou
            console.log(email, senha);
        }
    }
 


    return (
        <>
            <div className="login">
                <div className="conteudo">
                    <div className="titulo">
                        <Link to={'/'}> <img src={volta} alt="" /> </Link>
                        <h1>Login</h1>
                    </div>

                    <Container maxWidth="md">
                        <Box
                            sx={{
                                display: "flex",
                                flexDirection: "column",
                                justifyContent: "center",
                                // alignItems: "center",
                                mt: 10
                            }}
                        >
                            <TextField
                                required
                                margin="normal"
                                fullWidth
                                id="email"
                                label="Endereço de e-mail"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                onChange={pegarEmail}
                            />
                            <TextField
                                required
                                margin="normal"
                                fullWidth
                                name="senha"
                                label="Senha"
                                type="password"
                                id="senha"
                                onChange={pegarSenha}
                            />
                            { }
                            <Link to={'/'}>
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    sx={{ mt: 3, mb: 2, color: "white" }}
                                    onClick={verificacao}
                                >
                                    Sign In
                                </Button>
                            </Link>


                            <Box
                                sx={{
                                    display: "flex",
                                    flexDirection: "row",
                                    justifyContent: "space-between"
                                }}
                                width="100%"
                            >
                                <Link
                                    href="#"
                                    underline="none"
                                    className="link"
                                    fontSize={"15px"}
                                >
                                    Esqueceu a senha?
                                </Link>

                                <Link
                                    href="#"
                                    underline="none"
                                    className="link"
                                    fontSize={"15px"}
                                >
                                    Não tem conta? Cadastre-se.
                                </Link>
                            </Box>
                        </Box>
                    </Container>
                </div>

                <div className="image">
                    <img src={casa} alt="" />
                </div>

            </div>

        </>
    )

}

export default Signin
