import React, { useEffect } from "react";
import Fullcalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { useState } from "react";

import cantores from "../../midia/cantores.png";
import passaro from "../../midia/passaro.png";


function Calendar() {

  
  const [diaselec,Setdiaselect] = useState (
    { title: 'Dia da marmota', date: '2023-06-06', descricao: 'Um dia super legalzinho :)' },
  )
  const mes = ['janeiro','fevereiro','março','abril','maio','junho','julho','agosto','setembro','outubro','novembro','dezembro']
  let dia = 0


  const [eventos,SetEventos] = useState ([
  { title: 'Dia da marmota', date: '2023-06-07', descricao: 'Um dia super legalzinho :)' },
  { title: 'Dia do cururu', date: '2023-06-06', descricao: 'Mds que dia legal :o' },
  { title: 'Dia do coiso', date: '2023-06-06', descricao: 'Cuidado com o coiso' }
  ])



  const mostra = (info) => {
    const clickedDate = info.dateStr;
    const eventosDoDia = eventos.filter(evento => evento.date === clickedDate);

    Setdiaselect(eventosDoDia)


    dia = clickedDate.split("-")[2];
    console.log(clickedDate.split("-")[2])
    console.log(dia)
  }

  useEffect ( 
    () => {

    },
    [dia]
  )

  return (
    <>
      <div className="overlayDia">
        <div className="container-data">
          <div className="data">
            <div className="mes">Junho</div>
            <div className="dia">06</div>
          </div>
          <div className="eventos">
            <div className="evento">
              <div className="titulo">Dia da marmota</div>
              <div className="descricao"><p>aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</p></div>
            </div>
          </div>
        </div>
      </div>

      <div className="bloco-calendario">
        <img className="cantores" src={cantores} alt="" />
        <img className="passaro" src={passaro} alt="" />
        <h1>Eventos</h1>
        <p>Confira nosso calendário de eventos!</p>
        <div className="calendario">
          <div className="corpo-calendario" style={{ width: "50vw" }}>
            <Fullcalendar
              plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
              initialView={"dayGridMonth"}
              headerToolbar={{
                start: "title", // will normally be on the left. if RTL, will be on the right
                center: "",
                end: "today prev,next", // will normally be on the right. if RTL, will be on the left
              }}
              events= {eventos}
              locale='pt-br'
              height={"80vh"}
              buttonText={{
                today: 'Hoje'
              }}
              dateClick={mostra}
            />
          </div>
        </div>

      </div>

    </>

  );
}

export default Calendar;
