  const NossosDados = () => {
    const [numeroAcessos, setNumeroAcessos] = useState(100);
    const [isVisible, setIsVisible] = useState(false);
  
    useEffect(() => {
      const handleScroll = () => {
        const element = document.getElementById("contenedor-nossos-dados");
        if (element) {
          const rect = element.getBoundingClientRect();
          const isVisible = rect.top <= window.innerHeight && rect.bottom >= 0;
          setIsVisible(isVisible);
        }
      };
  
      window.addEventListener("scroll", handleScroll);
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, []);
  
    useEffect(() => {
      if (isVisible) {
        const incrementAcesso = () => {
          const acessos = parseInt(localStorage.getItem("acessos") || "100") + 1;
          localStorage.setItem("acessos", String(acessos));
          setNumeroAcessos(acessos);
        };
  
        incrementAcesso();
      }
    }, [isVisible]);
  
    return (
      <div className="rodape_NossosDados">
        <div className="conteudo-container">
          <div className="noticias-container">
            {/* Componente de notícias */}
          </div>
          <div id="contenedor-nossos-dados">
            {isVisible ? (
              <div className="calendario-header_Nossos_dados">
                <h1>Confira nossos Resultados</h1>
                <p>Pessoas que acessaram nosso site:</p>
                <div
                  className="numero-acessos"
                  style={{ fontSize: "60px", fontWeight: "bold" }}
                >
                  {numeroAcessos}
                </div>
              </div>
            ) : null}
          </div>
        </div>
      </div>
    );
  };
  

