import { Container, Box, Typography, TextField, Button } from "@mui/material"
import casa from '../../midia/casa-de-saberes.jpg'
import volta from "../../midia/volta.png"
import { Link } from "react-router-dom"

import { useContext } from "react"
import Administrador from "../Login_Contexto/ContextoLogin";
import { useState } from "react";
import { publicacoes} from "./publicacoes"


const NovaPublicacao = () => {
    const [nome, setNome] = useState('');

    const pegarNome = (event) => {
        setNome(event.target.value);
    };
    const publicar = () =>{
        // const novoObjeto = { id: publicacoes.length, titulo: nome };
        // publicacoes.push(novoObjeto);
        console.log(publicacoes)
    }


 


    return (
        <>
            <div className="nova-publicacao">
                <div className="conteudo">
                    <div className="titulo">
                        <Link to={'/PaginaPublicacao'}> <img src={volta} alt="" /> </Link>
                        <h1>Nova Publicação</h1>
                    </div>

                    <Container maxWidth="md" >
                        <Box
                            sx={{
                                display: "flex",
                                flexDirection: "column",
                                justifyContent: "center",
                                // alignItems: "center",
                                mt: 10
                            }}
                        >
                            <TextField
                                required
                                margin="normal"
                                fullWidth
                                id="email"
                                label="Coloque o nome da sua publicação"
                                name="email"
                                autoComplete="email"
                                autoFocus
                                onChange={pegarNome}
                            />
                            <Link >
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    sx={{ mt: 3, mb: 2, color: "white" }}
                                    onClick={publicar}
                                >
                                    Publicar
                                </Button>
                            </Link>
                        </Box>
                    </Container>
                </div>
            </div>

        </>
    )
}

export default NovaPublicacao
