import React from "react";
import { useState } from "react";

import lupa from "../../midia/lupa.png"
import x from "../../midia/volta3.png"

const Pesquisas = () => {
    const [linkForm,SetLinkForm] = useState ('https://docs.google.com/forms/d/e/1FAIpQLScAtF1ZGu-bONy2-xRUQXHq8F9IsgnVtXA-5pkGXrUyE1SYuQ/viewform?embedded=true')
    const [pesquisas,SetPesquisa] = useState ([
        {titulo:'Você conhece a Casa de Saberes Cego Aderaldo?', descricao:'Pesquisa para saber se nosso público-alvo realmente conhece nossa Casa de saberes', link:'<iframe src="https://docs.google.com/forms/d/e/1FAIpQLScAtF1ZGu-bONy2-xRUQXHq8F9IsgnVtXA-5pkGXrUyE1SYuQ/viewform?embedded=true" width="640" height="1187" frameborder="0" marginheight="0" marginwidth="0">Carregando…</iframe>'},
        {titulo:'Qual evento você e sua familia mais gostam de ir?', descricao:'Nós queremos saber seus eventos favoritos', link:'https://docs.google.com/forms/d/e/1FAIpQLScAtF1ZGu-bONy2-xRUQXHq8F9IsgnVtXA-5pkGXrUyE1SYuQ/viewform?embedded=true'},
        {titulo:'Qual o melhor dia para o evento de música?', descricao:'Queremos saber qual dia é o melhor para você comparecer no nosso evento de música!', link:'https://docs.google.com/forms/d/e/1FAIpQLScAtF1ZGu-bONy2-xRUQXHq8F9IsgnVtXA-5pkGXrUyE1SYuQ/viewform?embedded=true'},
    ])
    const [classe,SetClass] = useState ('')

    const PesquisaClicada = (link) => {
        SetLinkForm(link)
        const urlRegex = /src="([^"]*)"/;
        const match = linkForm.match(urlRegex);
        const url = match && match[1];
        SetLinkForm(url)
        SetClass('active')
        console.log(linkForm)
    }

    return(
        <>
        <div className="cabecalho">
            <h1 className="titulo"> Pesquisas</h1>
            <p className="subtitulo">Nós ajude a entender você da melhor forma!</p>
        </div>
        <div className={"overlayNovoEvento "+ classe}>
            <div className= {"formulario-container "+ classe}>
                <div className="fechar-formulario">
                    <img src={x} alt="" onClick={() => SetClass('')} />
                    <h1>Pesquisa</h1>
                </div> 
                <iframe src={linkForm} width="100%" height="90%" frameborder="0" marginheight="0" marginwidth="0">Carregando…</iframe>
            </div>
        </div>
        <div className="pesquisas">
            {pesquisas.map((pesquisa) =>
                <>
                    <div className="item-pesquisa" onClick={() => PesquisaClicada(pesquisa.link)}>
                        <div className="pesquisa">
                            <img src={lupa} alt="" />
                            <h1>{pesquisa.titulo}</h1>
                        </div>
                        <div className="descricao"><p>{pesquisa.descricao}</p></div>
                    </div>
                </> 
            )}
        </div>
            
        
        </>
    )
}

export default Pesquisas
