import React, { useState, useEffect } from 'react';
import { Document, Page } from 'react-pdf';
import { useParams } from 'react-router-dom';


import { Worker, Viewer } from '@react-pdf-viewer/core';

const Trabalho = () => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [isLoading, setIsLoading] = useState(true);

  const { linkPdf } = useParams("");

  const [pdfData, setPdfData] = useState(null);
  const [viewData, setViewData] = useState(null);

  const handleChange = (e) =>{

  }

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  useEffect(() => {
    const fetchPdf = async () => {
      try {
        const response = await fetch(`http://localhost:3001/pdf/${linkPdf}`);
        const blob = await response.blob();
        const url = URL.createObjectURL(blob);
        setPdfData(url);
        setIsLoading(false);
      } catch (error) {
        console.error('Erro ao obter o PDF:', error);
      }
    };

    fetchPdf();
  }, [linkPdf]);

  if (isLoading) {
    return <div>Carregando PDF...</div>;
  }

  return (
    <div style={{width:"100%",display:"flex",justifyContent:"center"}}>
      <div
      style={{
      
        width: '30%',
      }}
    >
      <Worker workerUrl="https://unpkg.com/pdfjs-dist@3.4.120/build/pdf.worker.min.js" >
        <Viewer fileUrl= {pdfData} style={{border: '1px solid rgba(0, 0, 0, 0.3)'}}/>
      </Worker>
    </div>
    </div>
    
  );
};

export default Trabalho;
