import * as React from 'react';

import { useState } from 'react';
import { Document, Page } from 'react-pdf';
import { useEffect } from 'react';
import axios from 'axios';

const Trabalho = ({ match }) =>{
  const File = match.params.linkPdf;
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  useEffect(
    () => {
      axios.get("http://localhost:3001/trabalhos/listar")
        .then(
          (response) => {
          }
        )
        .catch(error => console.log(error))
    }
    ,
    []
  )

  return (
    <div>
      <Document file="http://www.gestaoescolar.diaadia.pr.gov.br/arquivos/File/producoes_pde/md_christiane_maia_silveira.pdf" onLoadSuccess={onDocumentLoadSuccess}>
        <Page pageNumber={pageNumber} />
      </Document>
      <p>
        Page {pageNumber} of {numPages}
      </p>
    </div>
  );
}

export default Trabalho
