import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import SkipPreviousIcon from '@mui/icons-material/SkipPrevious';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import SkipNextIcon from '@mui/icons-material/SkipNext';

import capa from "../../midia/capa2.png"

export default function AudioPlayer() {
  const theme = useTheme();

  return (
    <Card sx={{ display: 'flex', backgroundColor:"#a12d2e", width:"100%" }}>
      <Box sx={{ display: 'flex', flexDirection: 'column' }}>
        <CardContent sx={{ flex: '1 0 auto' }}>
          <Typography component="div" variant="h5" sx={{ color:"#EAE8DB"}}>
            A Casa de Saberes Cego Aderaldo
          </Typography>
          <Typography variant="subtitle1" color="text.secondary" component="div" sx={{ color:"#EAE8DB"}}>
            Gabriel Okidoi
          </Typography>
        </CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', pl: 1, pb: 1 }}>
          <IconButton aria-label="play/pause" sx={{ color:"#EAE8DB"}}>
            <PlayArrowIcon sx={{ height: 38, width: 38 }} />
          </IconButton>
        </Box>
      </Box>
      <CardMedia
        component="img"
        sx={{ width: 151 }}
        image= {capa}
        alt="Live from space album cover"
      />
    </Card>
  );
}
