const Noticias = () => {
    const [data, setData] = useState([]);
    const carousel = useRef(null);
  
    useEffect(() => {
      fetch('http://localhost:3000/static/noticias.json')
        .then((response) => response.json())
        .then(setData);
    }, []);
  
    const handleLeftClick = (e) => {
      e.preventDefault();
      carousel.current.scrollLeft -= carousel.current.offsetWidth;
    };
  
    const handleRightClick = (e) => {
      e.preventDefault();
      carousel.current.scrollLeft += carousel.current.offsetWidth;
    };
  
    if (!data || !data.length) return null;
  
    return (
      <div className="bloco-calendario1">
        <div className="calendario-header">
          <h1> Notícias e Oportunidades</h1>
          <p>Quadro de notícias e oportunidade para você!</p>
        </div>
  
        <div className="container">
          <div className="logo"></div>
          <div className="carousel" ref={carousel}>
            {data.map((item) => {
              const { id, name, price, oldPrice, image, link } = item;
              return (
                <a href={link} className="item" key={id}>
                  <div className="image">
                    <img src={image} alt={name} />
                  </div>
                </a>
              );
            })}
          </div>
          <div className="buttons">
            <button onClick={handleLeftClick}>
              <img src="/static/images/216151_right_chevron_icon.png" alt="Scroll Left" />
            </button>
            <button onClick={handleRightClick}>
              <img src="/static/images/216151_right_chevron_icon.png" alt="Scroll Right" />
            </button>
          </div>
        </div>
      </div>
    );
  };

